package selenium;

public class Switch_case {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
char c = 'h';

switch(c)
{
case 'a':
System.out.println(c+" is a vowel");
break;

case 'e':
	System.out.println(c+" is a vowel");
	break;
	
case 'i':
	System.out.println(c+ " is a vowel");
	break;
	
case '0':
	System.out.println(c+" is a vowel");
	break;
	
case 'u':
	System.out.println(c+ " is a vowel");
	break;
	
	default:
		System.out.println(c+" is not a vowel");
}
	}
}
